    #define.CurrentVersion(1)
    #localmacro.CurrentList
        dummy
    #endmacro

}

Method.Source:
protected void update()
{
    SIMSStartTable      startTable;
    SIMSStartRunAction  startRunAction;
    int                 cnt;

    while select startTable
        order by SortOrder
        where startTable.RunOnStart == NoYes::Yes
    {
        info(startTable.getEnvUrl());

        if (startTable.StartAllTime == NoYes::No && startTable.ExecutedOn)
        {
            continue;
        }

        if (!startTable.checkIfLineCanRun())
        {
            continue;
        }

        try
        {
            if (startTable.DeleteLine == NoYes::Yes)
            {
                delLine.addEnd(startTable.RecId);
            }

            cnt++;
            startRunAction = SIMSStartRunAction::construct(startTable);
            startRunAction.run();
        }
        catch
        {
            if (startTable.ContinueOnError == NoYes::No)
            {
                Error(strFmt("Stop processing for Continue on error = NO! for record %1 %2", startTable.Description, startTable.SortOrder));
                return;
            }
        }

        if (startRunAction.parmHasErrors())
        {
            error(strfmt("Line %1 %2 has error", startTable.Description, startTable.SortOrder));
        }
        else
        {
            info(strfmt("Line %1 %2 processed", startTable.Description, startTable.SortOrder));
        }
    }
    info(strFmt("%1 processed lines", cnt));
}

/// <summary>
///    Contains the code that does the actual job of the class.
/// </summary>
public void run()
{
    #OCCRetryCount
    if (! this.validate())
        throw error("");

    this.update();

    SIMSStartTable      startTable;
    ListEnumerator      le = delLine.getEnumerator();
    ttsbegin;
    while (le.moveNext())
    {
        delete_from startTable
            where startTable.RecId == le.current();
    }
    ttscommit;

}

public boolean unpack(container packedClass)
{
    Version version = RunBase::getVersion(packedClass);
    ;
    switch (version)
    {
        case #CurrentVersion:
            [version,#CurrentList] = packedClass;
            break;
        default:
            return false;
    }

    return true;
}

