{
    /// <summary>
    /// Method run main update process. Need to be covered by ttsbegin/ttscommit and try/catch if nesessory
    /// </summary>
    str message;
}

Method.Source:
public void run()
{
    CustTable   custTable;
    select firstonly firstfast crosscompany custTable
        ;
    message = strFmt("@SIMSStartProcessing:CustTableAccountName", custTable.accountNum, custTable.name());
}

/// <summary>
/// Return status for the log
/// </summary>
/// <returns>Log message</returns>
public str resultMessage()
{
    return message;
}
