SourceCode.Declaration:
/// <summary>
/// Subscribe for AOS start processing
/// </summary>
public class SIMSStartActionServerRunHandler
{
}

Method.Source:
[Hookable(false), SubscribesTo(classStr(ApplicationStartupEventManager), staticDelegateStr(ApplicationStartupEventManager, onSystemStartup))]
internal static void OnSystemStartup()
{
    SIMSStartActionServerRunThread  startActionServerRunThread = SIMSStartActionServerRunThread::construct();
    BatchJob                        batchJob;

    select firstfast firstonly batchJob
        where batchJob.Caption  == SIMSStartActionServerRunThread::description()
           && (batchJob.Status   == BatchStatus::Executing
           || batchJob.Status   == BatchStatus::Waiting)
        ;
    if (!batchJob)
    {
        BatchHeader header = BatchHeader::construct();
        header.addTask(startActionServerRunThread);
        header.parmCaption(SIMSStartActionServerRunThread::description());
        header.parmExecutingBy('Admin');
        header.save();
    }
}
