/// <summary>
/// Run action for AOT class
/// </summary>
public class SIMSStartRunActionClass extends SIMSStartRunAction
{
}

Method.Source:
/// <summary>
/// update method
/// </summary>
/// <returns>if processing finish with error</returns>
protected boolean update()
{

    SIMSStartTable                      startTable  = SIMSStartTable::find(this.parmActionRecId());
    SIMSStartActionRunClassInterface    runClass    = new ClassFactory().createClass(className2Id(startTable.AOTClassName));
    boolean                             hasErrorsRet= true;

    try
    {
        runClass.run();
        this.parmResultMessage(runClass.resultMessage());
        hasErrorsRet = false;
    }
    catch
    {
        this.parmResultMessage(strFmt("@SIMSStartProcessing:ClassRaiseTheIssue", startTable.AOTClassName));
    }
    return hasErrorsRet;
}
