/// <summary>
/// Run runbase class action for AOT class
/// </summary>
public class SIMSStartRunActionRunBaseClass extends SIMSStartRunActionClass
{
}

Method.Source:
/// <summary>
/// update method
/// </summary>
/// <returns>if processing finish with error</returns>
protected boolean update()
{

    SIMSStartTable      startTable  = SIMSStartTable::find(this.parmActionRecId());
    SysRunable          runClass    = new ClassFactory().createClass(className2Id(startTable.AOTClassName));
    boolean             hasErrorsRet= true;

    try
    {
        runClass.run();
        this.parmResultMessage(strFmt("@SIMSStartProcessing:ClassRunSucsessfully", startTable.AOTClassName));
        hasErrorsRet = false;
    }
    catch
    {
        this.parmResultMessage(strFmt("@SIMSStartProcessing:ClassRaiseTheIssue", startTable.AOTClassName));
    }
    return hasErrorsRet;
}
