/// <summary>
/// This table store sub admins (who can approve own task for itself)
/// </summary>
public class SIMSStartSubAdmins extends common
{

}

Method.Source:
/// <summary>
/// If user can approve AOS start functionality
/// </summary>
/// <param name = "_userId">User Id for check</param>
/// <returns>True if user can</returns>
public static boolean isUserCanApproveTheLines(UserId _userId = curUserId())
{
    SIMSStartSubAdmins  startSubAdmins;

    select firstonly firstfast startSubAdmins
        where startSubAdmins.UserId     == _userId
           && startSubAdmins.Enabled    == NoYes::Yes
        ;
    return startSubAdmins.RecId != 0;
}

public void insert()
{
    SIMSStartLog::addLog(strFmt("@SIMSStartProcessing:SubAdminsInsertTableRecord", this.UserId, curUserId()));
    super();                                                         
}

public void update()
{
    SIMSStartLog::addLog(strFmt("@SIMSStartProcessing:SubAdminsUpdateTableRecord", this.orig().UserId, this.UserId, curUserId()));
    super();
}

public void delete()
{
    SIMSStartLog::addLog(strFmt("@SIMSStartProcessing:SubAdminsDeleteTableRecord", this.UserId, curUserId()));
    super();
}

/// <summary>
/// Check if URL form called with Admin user parameter
/// </summary>
/// <returns>True if call with admin param</returns>
public static boolean isSuperUrlCall()
{
    URLUtility urlUtility = new URLUtility();

    str user = urlUtility.getQueryParamValue('user');

    return user == 'Admin';
}

/// <summary>
/// Display method for form caption
/// </summary>
/// <returns>return form caption</returns>
[SysClientCacheDataMethodAttribute(true)]
public str caption()
{
    if (SIMSStartSubAdmins::isSuperUrlCall())
    {
        return strFmt("@SIMSStartProcessing:AdminMode", curUserId());
    }
    return curUserId();
}
